Ext.define('Osbb.view.sprav.TabNewAddressViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.tabnewaddress',

    onCbAddressOrgSelect: function(combo, record, eOpts) {
        var  chbDeleteAddress = Ext.getCmp('chbDeleteAddress');
        var  fmDeleteAddress = Ext.getCmp('fmDeleteAddress');

        chbDeleteAddress.setVisible(true);
        chbDeleteAddress.setValue(0);

        fmDeleteAddress.setVisible(false);

    },

    onCbHouseOrgSelect: function(combo, record, eOpts) {
        //in use
        var me=this;
        /*
        COMPONENT
        */
        var cbAddressOrg = Ext.getCmp("cbAddressOrg");
        var  fmDeleteAddress = Ext.getCmp('fmDeleteAddress');
        var  delHouseId = fmDeleteAddress.getForm().findField('delHouseId');
        //var viewHouses = me.getListHousesData();
        /*
        STORE
        */
        var stUser = Ext.data.StoreManager.get("StUser");
        var StAddressOrg = Ext.data.StoreManager.get("StAddressOrg");
        /*
        LOGIN & PASSWORD
        */
        var values =stUser.getAt(0);
        var login = values.get('login');
        var password = values.get('password');
        /*
        LOGIKA
        */
        //cbRaion.clearValue();
        if (record) {
            delHouseId.setValue(record.get('house_id'));

            StAddressOrg.load({
                params: {
                    what:'kvartira',
                    house_id: record.get('house_id'),
                    login:login,
                    password:password
                },
                scope: this
            });
        }
    }

});
