    onComboboxSelect21: function(combo, record, eOpts) {
        //in use

        //STORE
        var stUser = Ext.data.StoreManager.get("StUser");
        var StTarif = Ext.data.StoreManager.get("StTarif");
        var tarif = Ext.getCmp('tarVoda');
        var viborTarif = Ext.getCmp('cbTarifVoda');
        var form = combo.findParentByType('form');
        //var data = form.getForm().findField('data').getValue();

        //LOGIN & PASSWORD

        var values =stUser.getAt(0);
        var login = values.get('login');
        var password = values.get('password');

        //LOGIKA
        if (record) {
            StTarif.load({
                params: {
                    what:'TarifFromHouse',
                    login:login,
                    password:password,
                    //data:data,
                    house_id: record.get('house_id')
                },
                callback: function(records,operation,success){
                    if(success){
                        viborTarif.clearValue();
                        viborTarif.setDisabled(true);
                        tarif.setValue(0);
                    }
                },
                scope:this
            });
        }
    },

