Ext.define('Osbb.view.sprav.WinAddGasomerViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.winaddgasomer',

    onChkExistentGasomerChange: function(field, newValue, oldValue, eOpts) {
        var cnt = Ext.getCmp('fcntNewGasomer');
        var cb = Ext.getCmp('cbExistentGasomer');
        var form = field.findParentByType('form');

        if (newValue) {
            cnt.hide();
            cb.show();
            cb.clearValue();

            form.down('#btAddGasomer').setDisabled(true);

        }
        else {
            cnt.show();
            cb.hide();
            form.down('#btAddGasomer').setDisabled(false);

        }

    },

    onCbExistentGasomerSelect: function(combo, record, eOpts) {
        var form = combo.findParentByType('form');
        form.down('#btAddGasomer').setDisabled(false);

    },

    onTextfieldChange: function(field, newValue, oldValue, eOpts) {
        var form = field.findParentByType('form');
        form.down('#btAddGasomer').setDisabled(false);

    }

});
